import React from "react";

function FabricList({fabric: {FabricId, FabricType, Color, PatternDesc, 
    Yardage, Width, SourceName, ProjectName, ScrapStatus} }) {
        const widthFormat = new Intl.NumberFormat({maximumFractionDigits: 0});
    return (
    <tr key={FabricId}>
        <td>{FabricType}</td>
        <td>{Color}</td>
        <td>{PatternDesc}</td>
        <td className="text-end">{Yardage}</td>
        <td className="text-end">{widthFormat.format(Width)}"</td>
        <td>{SourceName}</td>
        <td>{ProjectName}</td>
        <td><input type="checkbox" checked={ScrapStatus}></input></td>
    </tr>
    );
}

export default FabricList;
