import './App.css';
import { useState } from "react";

function App() {

  const [fabricType, setFabricType] = useState('');
  const [fabricColor, setFabricColor] = useState('');
  const [fabricPattern, setFabricPattern] = useState('');
  const [yardage, setYardage] = useState('');
  const [width, setWidth] = useState('');
  const [fabricSource, setFabricSource] = useState('');
  const [scrapStatus, setScrapStatus] = useState('');
  const [intendedProject, setIntendedProject] = useState('');
  return (
    <div className="App">
      <div className="info">
        <label>Fabric Type:</label>
        <input type="text"/>
        <label>Fabric Color:</label>
        <input type="text"/>
        <label>Fabric Pattern:</label>
        <input type="text"/>
        <label>Yardage:</label>
        <input type="number"/>
        <label>Width:</label>
        <input type="number"/>
        <label>Fabric Source:</label>
        <input type="text"/>
        <label>Is this fabric a scrap peice?</label>
        <input type="checkbox"/>
        <label>Intended Project:</label>
        <input type="text"/>
        <button>Add Fabric</button>
      </div>
    </div>
  );
}

export default App;
