import '../App.css';
import React, { useState, useEffect } from "react";
import FabricDataService from "../services/fabricServices";

function ViewStash() {
    const [stash, setStash] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const widthFormat = new Intl.NumberFormat({maximumFractionDigits: 0});

    useEffect(() => {
      const getStash = async () => {
        try {
          const response = await FabricDataService.getAll();
          console.log(response);
          setStash(response.data);
          setError(null);
        } catch (err) {
          setError(err.message);
          setStash(null);
        } finally {
          setLoading(false);
        }
      };
      getStash();
    }, []);
    console.log(stash);

    function Fabric({fabric: {FabricId, FabricType, Color, PatternDesc, 
      Yardage, Width, SourceName, ProjectName, ScrapStatus} }) {
        // return (
        //   <tr key={FabricId}>
        //       <td>{FabricType}</td>
        //       <td>{Color}</td>
        //       <td>{PatternDesc}</td>
        //       <td>{Yardage}</td>
        //       <td>{Width}</td>
        //       <td>{SourceName}</td>
        //       <td>{ProjectName}</td>
        //       <td>{ScrapStatus}</td>
        //   </tr>
        // );
      }

    return (
      <div>
          <table className="table table-bordered table-striped">
              <thead className="thead-dark">
                  <tr>
                      <th scope="col">Fabric Type</th>
                      <th scope="col">Color</th>
                      <th scope="col">Pattern</th>
                      <th scope="col">Yardage</th>
                      <th scope="col">Width</th>
                      <th scope="col">Source</th>
                      <th scope="col">Intended Project</th>
                      <th scope="col">Scrap Status</th>
                  </tr>
                </thead>
                <tbody>
                  {stash.map(({fabric: {FabricId, FabricType, Color, PatternDesc, 
      Yardage, Width, SourceName, ProjectName, ScrapStatus} }) => 
                  {
                    return (
                      <tr key={FabricId} onClick={console.log(FabricId)}>
                          <td>{FabricType}</td>
                          <td>{Color}</td>
                          <td>{PatternDesc}</td>
                          <td className="text-end">{Yardage}</td>
                          <td className="text-end">{widthFormat.format(Width)}"</td>
                          {/* <td>{SourceName}</td>
                          <td>{ProjectName}</td> */}
                          <td className="text-center"><input type="checkbox" checked={ScrapStatus}></input></td>
                      </tr>
                  );            
                  }

                    )}
                </tbody>
          </table>
      </div>
    );
  }


export default ViewStash;
