import './App.css';
import { useState } from "react";
import { Card } from "react-bootstrap";
import Axios from 'axios';

function CreateFabric() {
  const [show, setShow] = useState(true);
  const [status, setStatus] = useState('');
    const [fabricType, setFabricType] = useState('');
    const [color, setColor] = useState('');
    const [fabricPattern, setFabricPattern] = useState('');
    const [yardage, setYardage] = useState(0);
    const [width, setWidth] = useState(0);
    const [fabricSource, setFabricSource] = useState('');
    const [scrapStatus, setScrapStatus] = useState(false);
    const [intendedProject, setIntendedProject] = useState('');

  const addFabric = () => {
    Axios.post('http://localhost:3001/create', {
      FabricTypeId: fabricType,
      Color: color,
      PatternId: fabricPattern,
      Yardage: yardage,
      Width: width,
      FabricSourceId: fabricSource,
      ScrapStatus: scrapStatus,
      IntendedProject: intendedProject
    }).then(() => {
      console.log("Success");
    });
  }

  const clearForm = () => {
  }

  return (
    <Card style={{ backgroundColor: "lightgray", color: "black", width: "600px" }}>
    <Card.Header>Create Account</Card.Header>
    <Card.Body>
        {show ? ( 
            <>
                <form id="add-fabric-form">
                    <div className="form-group">
                        <label>Fabric Type:</label>
                        <input type="number" className="form-control" onChange={(event) => {setFabricType(event.target.value);}}/>
                        <label>Fabric Color(s):</label>
                        <input type="text" className="form-control" onChange={(event) => {setColor(event.target.value);}}/>
                        <label>Fabric Pattern:</label>
                        <input type="number" className="form-control" onChange={(event) => {setFabricPattern(event.target.value);}}/>
                        <label>Yardage:</label>
                        <input type="number" className="form-control" id="yardage" onChange={(event) => {setYardage(event.target.value);}}/>
                        <label>Width:</label>
                        <input type="number" className="form-control" id="width" onChange={(event) => {setWidth(event.target.value);}}/>
                        <label>Fabric Source:</label>
                        <input type="number" className="form-control" onChange={(event) => {setFabricSource(event.target.value);}}/>
                        <label>Intended Project:</label>
                        <input type="text" className="form-control" id="intendedProject" onChange={(event) => {setIntendedProject(event.target.value);}}/>
                    </div>
                    <div className="form-check">
                        <input type="checkbox" className="form-check-input" id="scrap-checkbox" onChange={(event) => {setScrapStatus(event.target.checked);}}/>
                        <label className="form-check-label">This fabric is a scrap.</label>
                    </div>
                    <br />
                    <button type="button" className="btn btn-info" onClick={addFabric}>Add Fabric</button>
                </form>
            {status}
                    </>
                    ):( 
                    <>
                        <h5>Success</h5>
                        <button type="submit" className="btn btn-light" onClick={clearForm}>Add another account</button> 
                    </> 
                    )}
            </Card.Body>
        </Card>
  );

}

export default CreateFabric;
