import '../App.css';
import { useState } from "react";
import FabricDataService from "../services/fabricServices";
import { Card } from "react-bootstrap";
import Axios from 'axios';

function AddFabric() {
  const initialFabricState = {
      FabricTypeId: null,
      Color: "",
      PatternId: null,
      Yardage: null,
      Width: null,
      FabricSourceId: "",
      ScrapStatus: false,
      ProjectId: null
  };

  const [fabric, setFabric] = useState(initialFabricState)
  const [submitted, setSubmitted] = useState(false);
  const handleInputChange = event => {
    const { name, value } = event.target;
    setFabric({ ...fabric, [name]: value });
  };

    const saveFabric = () => {
      let data = {
          FabricTypeId: fabric.fabricType,
          Color: fabric.color,
          PatternId: fabric.fabricPattern,
          Yardage: fabric.yardage,
          Width: fabric.width,
          FabricSourceId: fabric.fabricSource,
          ScrapStatus: fabric.scrapStatus,
          ProjectId: fabric.projectId
      };
      FabricDataService.create(data).then(response => {
        setFabric({
            FabricTypeId: response.data.fabricType,
            Color: response.data.color,
            PatternId: response.data.fabricPattern,
            Yardage: response.data.yardage,
            Width: response.data.width,
            FabricSourceId: response.data.fabricSource,
            ScrapStatus: response.data.scrapStatus,
            ProjectId: response.data.projectId
        });
        setSubmitted(true);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
    };

    const newFabric = () => {
      setFabric(initialFabricState);
      setSubmitted(false);
    };

  return (
    <Card style={{ backgroundColor: "lightgray", color: "black", width: "400px" }}>
    <Card.Header>Add a Fabric</Card.Header>
    <Card.Body>
        {submitted ? ( 
            <>
                <h5>Success! <br/>
                  Fabric has been added to stash.</h5>
                <button type="submit" className="btn btn-light" onClick={newFabric}>Add another fabric</button> 
                    </>
                    ):( 
                    <>
                <form id="add-fabric-form">
                    <div className="form-group">
                        <label>Fabric Type:</label>
                        <input type="number" className="form-control" value={fabric.fabricType} name="fabricType" onChange={handleInputChange}/>
                        <label>Fabric Color(s):</label>
                        <input type="text" className="form-control" value={fabric.color} name="color" onChange={handleInputChange}/>
                        <label>Fabric Pattern:</label>
                        <input type="number" className="form-control" value={fabric.fabricPattern} name="fabricPattern" onChange={handleInputChange}/>
                        <label>Yardage:</label>
                        <input type="number" className="form-control" id="yardage" value={fabric.yardage} name="yardage" onChange={handleInputChange}/>
                        <label>Width:</label>
                        <input type="number" className="form-control" id="width" value={fabric.width} name="width" onChange={handleInputChange}/>
                        <label>Fabric Source:</label>
                        <input type="number" className="form-control" value={fabric.fabricSource} name="fabricSource" onChange={handleInputChange}/>
                        <label>Intended Project:</label>
                        <input type="text" className="form-control" id="projectId" value={fabric.projectId} name="peojectId" onChange={handleInputChange}/>
                    </div>
                    <div className="form-check">
                        <input type="checkbox" className="form-check-input" id="scrap-checkbox" value={fabric.scrapStatus} name="scrapStatus" onChange={handleInputChange}/>
                        <label className="form-check-label">This fabric is a scrap.</label>
                    </div>
                    <br />
                    <button type="button" className="btn btn-info" onClick={saveFabric}>Add Fabric</button>
                </form>
                    </> 
                    )}
            </Card.Body>
        </Card>
  );

}

export default AddFabric;
