import '../App.css';
import FabricList from './fabricList';
import React, { useState, useEffect } from "react";
import FabricDataService from "../services/fabricServices";

function ViewStash() {
    const [stash, setStash] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
      const getStash = async () => {
        try {
          const response = await FabricDataService.getAll();
          setStash(response.data);
          setError(null);
        } catch (err) {
          setError(err.message);
          setStash(null);
        } finally {
          setLoading(false);
        }
      };
      getStash();
    }, []);

    return (
      <div>
          <table className="table table-bordered table-striped">
              <thead className="thead-dark">
                  <tr>
                      <th scope="col">Fabric Type</th>
                      <th scope="col">Color</th>
                      <th scope="col">Pattern</th>
                      <th scope="col" className="text-end">Yardage</th>
                      <th scope="col" className="text-end">Width</th>
                      {/* <th scope="col">Source</th>
                      <th scope="col">Intended Project</th> */}
                      <th scope="col">Scrap</th>
                  </tr>
                </thead>
                <tbody>
                  {stash.map(fabric => <FabricList key={FabricList.FabricId} fabric={fabric} />)}
                </tbody>
          </table>
      </div>
    );
  }


export default ViewStash;
