import './App.css';
import { useState } from "react";

function App() {

  const [fabricType, setFabricType] = useState('');
  const [fabricColor, setFabricColor] = useState('');
  const [fabricPattern, setFabricPattern] = useState('');
  const [yardage, setYardage] = useState(0);
  const [width, setWidth] = useState(0);
  const [fabricSource, setFabricSource] = useState('');
  const [scrapStatus, setScrapStatus] = useState(false);
  const [intendedProject, setIntendedProject] = useState('');

  // const displayInfo = () => {
  //   console.log(fabricType + fabricColor + fabricPattern + yardage + width + fabricSource + scrapStatus + intendedProject);
  // }

  return (
    <div className="App">
      <div className="info">
        <label>Fabric Type:</label>
        <input type="text" onChange={(event) => {setFabricType(event.target.value);}}/>
        <label>Fabric Color:</label>
        <input type="text" onChange={(event) => {setFabricColor(event.target.value);}}/>
        <label>Fabric Pattern:</label>
        <input type="text" onChange={(event) => {setFabricPattern(event.target.value);}}/>
        <label>Yardage:</label>
        <input type="number" onChange={(event) => {setYardage(event.target.value);}}/>
        <label>Width:</label>
        <input type="number" onChange={(event) => {setWidth(event.target.value);}}/>
        <label>Fabric Source:</label>
        <input type="text" onChange={(event) => {setFabricSource(event.target.value);}}/>
        <label>Is this fabric a scrap?</label>
        <input type="checkbox" onChange={(event) => {setScrapStatus(event.target.value);}}/>
        <label>Intended Project:</label>
        <input type="text" onChange={(event) => {setIntendedProject(event.target.value);}}/>
        <button>Add Fabric</button>
      </div>
    </div>
  );
}

export default App;
