import './App.css';
import { useState } from "react";
import Axios from 'axios';

function App() {

  const [fabricType, setFabricType] = useState('');
  const [color, setColor] = useState('');
  const [fabricPattern, setFabricPattern] = useState('');
  const [yardage, setYardage] = useState(0);
  const [width, setWidth] = useState(0);
  const [fabricSource, setFabricSource] = useState('');
  const [scrapStatus, setScrapStatus] = useState(false);
  const [intendedProject, setIntendedProject] = useState('');

  const addFabric = () => {
    console.log(fabricType)
    Axios.post('http://localhost:3001/create', {
      FabricTypeId: fabricType,
      Color: color,
      PatternId: fabricPattern,
      Yardage: yardage,
      Width: width,
      FabricSourceId: fabricSource,
      ScrapStatus: scrapStatus,
      IntendedProject: intendedProject
    }).then(() => {
      console.log("Success");
    });
  }

  // const displayInfo = () => {
  //   console.log(fabricType + color + fabricPattern + yardage + width + fabricSource + scrapStatus + intendedProject);
  // }

  return (
    <div className="App">
      <div className="info">
        <label>Fabric Type:</label>
        <input type="number" onChange={(event) => {setFabricType(event.target.value);}}/>
        <label>Fabric Color:</label>
        <input type="text" onChange={(event) => {setColor(event.target.value);}}/>
        <label>Fabric Pattern:</label>
        <input type="number" onChange={(event) => {setFabricPattern(event.target.value);}}/>
        <label>Yardage:</label>
        <input type="number" onChange={(event) => {setYardage(event.target.value);}}/>
        <label>Width:</label>
        <input type="number" onChange={(event) => {setWidth(event.target.value);}}/>
        <label>Fabric Source:</label>
        <input type="number" onChange={(event) => {setFabricSource(event.target.value);}}/>
        <label>Is this fabric a scrap?</label>
        <input type="checkbox" onChange={(event) => {setScrapStatus(event.target.checked);}}/>
        <label>Intended Project:</label>
        <input type="text" onChange={(event) => {setIntendedProject(event.target.value);}}/>
        <button onClick={addFabric}>Add Fabric</button>
      </div>
      <div className="fabrics">
        <button>Show Fabrics</button>
      </div>

    </div>
  );
}

export default App;
